import prisma from '../utils/prisma.js';
import { getCurrentDate } from '../utils/dateUtils.js';
import { MemberMessage as PrismaMemberMessage, Prisma } from '@prisma/client'; // Importing Member type and Prisma types
import memberRepository from './member.repository.js';

export interface TransformedMessage {
    /**
     * Lista lanova koji su proitali poruku (za admin prikaz)
     * Svaki objekt sadri member_id i read_at (ako je proitao)
     */
    read_by?: Array<{ member_id: number, read_at: Date | null, full_name: string | null }>;

    message_id: number;
    member_id: number | null;
    message_text: string;
    created_at: Date | null; // Doputamo null vrijednost za created_at
    sender_id: number | null;
    recipient_id: number | null;
    recipient_type: PrismaMemberMessage['recipient_type'];
    sender_type: PrismaMemberMessage['sender_type'];
    currentUserStatus?: 'unread' | 'read' | 'archived';
    currentUserReadAt?: Date | null;
    sender?: {
        member_id: number;
        first_name: string | null;
        last_name: string | null;
        full_name: string | null;
    } | null;
}

// Using shared prisma client

const memberMessageRepository = {
    async findById(messageId: number): Promise<PrismaMemberMessage | null> {
        // mapToMemberMessage e biti uklonjen ili refaktoriran
        // Direktno vraanje raw objekta ili prilagoeno mapiranje kasnije
        return prisma.memberMessage.findUnique({
            where: { message_id: messageId },
        });
    },

    async create(memberId: number, messageText: string): Promise<TransformedMessage> {
        // 1. Kreiraj osnovnu poruku
        const newMessage = await prisma.memberMessage.create({
            data: {
                message_text: messageText,
                sender_id: memberId,
                sender_type: 'member',
                recipient_type: 'member_administrator', // Poruka je namijenjena ulozi administratora
                member_id: memberId, // Odravamo ovo polje kao u staroj logici, povezuje s MemberMessage.member
            },
        });

        // 2. Dohvati sve administratore i superusere
        const adminAndSuperUserRoles = ['member_administrator', 'member_superuser'];
        const recipientMemberIds = await memberRepository.findMemberIdsByRoles(adminAndSuperUserRoles);

        // 3. Kreiraj MessageRecipientStatus za svakog primatelja
        if (recipientMemberIds.length > 0) {
            const recipientStatusData = recipientMemberIds.map(adminId => ({
                message_id: newMessage.message_id,
                recipient_member_id: adminId,
                status: 'unread', // Inicijalno neproitano
            }));
            await prisma.messageRecipientStatus.createMany({
                data: recipientStatusData,
            });
        }
        // Transformiraj poruku u TransformedMessage format s currentUserStatus i currentUserReadAt poljima
        const transformedMessage: TransformedMessage = {
            ...newMessage,
            currentUserStatus: 'unread',
            currentUserReadAt: null,
            sender: null // Moemo dodati podatke o poiljatelju ako je potrebno
        };

        return transformedMessage;
    },

    async createAdminMessage({
        senderId,
        messageText,
        recipientType = 'member',
        senderType = 'member_administrator',
        recipientId, // Koristi se samo ako je recipientType 'member'
        recipientMemberIds: inputRecipientMemberIds, // Koristi se samo ako je recipientType 'group'
    }: {
        senderId: number;
        messageText: string;
        recipientType?: 'member' | 'group' | 'all';
        senderType?: 'member_administrator' | 'member_superuser';
        recipientId?: number;
        recipientMemberIds?: number[];
    }): Promise<TransformedMessage> {
        const newMessage = await prisma.memberMessage.create({
            data: {
                message_text: messageText,
                sender_id: senderId,
                sender_type: senderType,
                recipient_id: recipientType === 'member' ? recipientId : null, // recipient_id je ID lana za 'member', inae null
                recipient_type: recipientType,
            },
        });

        let finalRecipientIds: number[] = [];

        if (recipientType === 'all') {
            const allMembers = await memberRepository.findAllReceivableMemberIds(senderId);
            finalRecipientIds = allMembers;
        } else if (recipientType === 'group') {
            if (!inputRecipientMemberIds || inputRecipientMemberIds.length === 0) {
                throw new Error('Za grupnu poruku potrebno je navesti primatelje.');
            }
            finalRecipientIds = inputRecipientMemberIds.filter(id => id !== senderId);
        } else if (recipientType === 'member' && recipientId) {
            finalRecipientIds = [recipientId];
        } else {
            throw new Error('Nevaei tip primatelja ili nedostaju ID-evi primatelja.');
        }

        if (finalRecipientIds.length > 0) {
            const recipientStatusData = finalRecipientIds.map(memberId => ({
                message_id: newMessage.message_id,
                recipient_member_id: memberId,
                status: 'unread', // Inicijalno neproitano
            }));
            await prisma.messageRecipientStatus.createMany({
                data: recipientStatusData,
                skipDuplicates: true, // Ignoriraj duplikate ako postoje
            });
        }

        // Transformiraj poruku u TransformedMessage format
        const transformedMessage: TransformedMessage = {
            ...newMessage,
            currentUserStatus: 'unread',
            currentUserReadAt: null,
            sender: null // Moemo dodati podatke o poiljatelju ako je potrebno
        };

        return transformedMessage;
    },

    async getAllForAdmin(currentMemberId: number): Promise<TransformedMessage[]> {
        const messagesData = await prisma.memberMessage.findMany({
            where: {
                // Dohvati poruke za koje postoji status za trenutnog korisnika
                recipient_statuses: {
                    some: {
                        recipient_member_id: currentMemberId,
                        // Opcionalno: filtriraj da se ne prikazuju arhivirane poruke ovdje
                        // status: { not: 'archived' }
                    },
                },
            },
            include: {
                sender: { // Podaci o poiljatelju
                    select: { member_id: true, first_name: true, last_name: true, full_name: true },
                },
                recipient_statuses: { // Statusi za trenutnog korisnika
                    where: { recipient_member_id: currentMemberId },
                    select: { status: true, read_at: true },
                },
                // Opcionalno: ako elimo znati sve primatelje poruke (npr. za prikaz 'All Members' ili liste grupe)
                // _count: { select: { recipient_statuses: true } } // Ukupan broj primatelja ove poruke
            },
            orderBy: { created_at: 'desc' },
        });

        // Definiranje tipa za payload s ukljuenim relacijama
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
              where: { recipient_member_id: currentMemberId }, // Osiguravamo da je where ovdje
              select: { status: true, read_at: true }
            },
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        // Mapiranje rezultata
        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            const recipientStatus = msg.recipient_statuses[0]; // Trebao bi biti samo jedan za currentMemberId
            return {
                message_id: msg.message_id,
                member_id: msg.member_id, // Originalno polje, ako se koristi
                message_text: msg.message_text,
                created_at: msg.created_at,
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type,
                sender_type: msg.sender_type,
                currentUserStatus: recipientStatus?.status as ('unread' | 'read' | 'archived' | undefined),
                currentUserReadAt: recipientStatus?.read_at,
                sender: msg.sender ? {
                    member_id: msg.sender.member_id,
                    first_name: msg.sender.first_name,
                    last_name: msg.sender.last_name,
                    full_name: msg.sender.full_name
                } : null,
            };
        });
    },

    async getByMemberId(memberId: number): Promise<TransformedMessage[]> {
        // memberId je ovdje isto to i currentMemberId za kojeg dohvaamo poruke
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
              select: { status: true, read_at: true, recipient_member_id: true } 
            },
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        const messagesData = await prisma.memberMessage.findMany({
            where: {
                recipient_statuses: {
                    some: {
                        recipient_member_id: memberId,
                        // status: { not: 'archived' } // Opcionalno, ako ne elimo arhivirane
                    },
                },
            },
            include: {
                sender: messageWithDetailsPayload.include.sender,
                recipient_statuses: {
                    where: { recipient_member_id: memberId },
                    select: messageWithDetailsPayload.include.recipient_statuses.select
                }
            },
            orderBy: { created_at: 'desc' },
        });

        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            const recipientStatus = msg.recipient_statuses[0]; 
            return {
                message_id: msg.message_id,
                member_id: msg.member_id, 
                message_text: msg.message_text,
                created_at: msg.created_at, 
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type, 
                sender_type: msg.sender_type, 
                currentUserStatus: recipientStatus?.status as ('unread' | 'read' | 'archived' | undefined),
                currentUserReadAt: recipientStatus?.read_at, 
                sender: msg.sender ? {
                    member_id: msg.sender.member_id,
                    first_name: msg.sender.first_name,
                    last_name: msg.sender.last_name,
                    full_name: msg.sender.full_name
                } : null,
            };
        });
    },

    async getMessagesSentByAdmin(adminId: number): Promise<TransformedMessage[]> {
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
            select: { 
              status: true, 
              read_at: true, 
              recipient_member_id: true,
              member: { select: { full_name: true } } // Dodano za dohvaanje imena primatelja
            } 
          } // Ukljuujemo radi sukladnosti s TransformedMessage
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        const messagesData = await prisma.memberMessage.findMany({
            where: {
                sender_id: adminId,
                sender_type: { in: ['member_administrator', 'member_superuser'] },
            },
            include: messageWithDetailsPayload.include,
            orderBy: { created_at: 'desc' },
        });

        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            // Za poslane poruke, currentUserStatus i currentUserReadAt nisu direktno primjenjivi
            // na isti nain kao za primljene. Ostavljamo ih kao undefined ili ih moemo izostaviti
            // ako TransformedMessage suelje to doputa (opcionalni su).
            return {
                message_id: msg.message_id,
                read_by: (msg.recipient_statuses || []).map(rs => ({
                    member_id: rs.recipient_member_id,
                    read_at: rs.read_at,
                    full_name: rs.member?.full_name || null // Dodano ime primatelja
                })),
                member_id: msg.member_id,
                message_text: msg.message_text,
                created_at: msg.created_at,
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type,
                sender_type: msg.sender_type,
                currentUserStatus: undefined, // Nije relevantno za poslane poruke na ovaj nain
                currentUserReadAt: undefined, // Nije relevantno za poslane poruke na ovaj nain
                sender: msg.sender ? {
                    member_id: msg.sender.member_id,
                    first_name: msg.sender.first_name,
                    last_name: msg.sender.last_name,
                    full_name: msg.sender.full_name
                } : null,
                // recipient_statuses: msg.recipient_statuses // Moe se dodati ako je potrebno prikazati statuse svih primatelja
            };
        });
    },

    async getSentMessagesByMemberId(memberId: number): Promise<TransformedMessage[]> {
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
              select: { status: true, read_at: true, recipient_member_id: true }
            },
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        const messagesData = await prisma.memberMessage.findMany({
            where: {
                sender_id: memberId,
            },
            include: messageWithDetailsPayload.include,
            orderBy: { created_at: 'desc' },
        });

        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            // Pronai status za trenutnog korisnika (poiljatelja)
            const currentUserStatus = msg.recipient_statuses.find(rs => rs.recipient_member_id === memberId);

            return {
                message_id: msg.message_id,
                member_id: msg.member_id,
                message_text: msg.message_text,
                created_at: msg.created_at,
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type,
                sender_type: msg.sender_type,
                currentUserStatus: currentUserStatus ? currentUserStatus.status as ('unread' | 'read' | 'archived' | undefined) : undefined,
                currentUserReadAt: currentUserStatus ? currentUserStatus.read_at : undefined,
                sender: msg.sender,
            };
        });
    },

    async getAdHocGroupMessagesForMember(currentMemberId: number): Promise<TransformedMessage[]> {
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
              select: { status: true, read_at: true, recipient_member_id: true }
            },
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        const messagesData = await prisma.memberMessage.findMany({
            where: {
                recipient_type: 'group',
                // recipient_id bi trebao biti null za ad-hoc grupne poruke, pa ga ne filtriramo ovdje
                recipient_statuses: {
                    some: {
                        recipient_member_id: currentMemberId,
                        // status: { not: 'archived' } // Opcionalno, ako elimo izuzeti arhivirane
                    },
                },
            },
            include: {
                sender: messageWithDetailsPayload.include.sender,
                recipient_statuses: {
                    where: { recipient_member_id: currentMemberId },
                    select: messageWithDetailsPayload.include.recipient_statuses.select
                }
            },
            orderBy: { created_at: 'desc' },
        });

        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            const recipientStatus = msg.recipient_statuses.find(rs => rs.recipient_member_id === currentMemberId) || msg.recipient_statuses[0];
            return {
                message_id: msg.message_id,
                member_id: msg.member_id,
                message_text: msg.message_text,
                created_at: msg.created_at,
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type,
                sender_type: msg.sender_type,
                currentUserStatus: recipientStatus?.status as ('unread' | 'read' | 'archived' | undefined),
                currentUserReadAt: recipientStatus?.read_at,
                sender: msg.sender ? {
                    member_id: msg.sender.member_id,
                    first_name: msg.sender.first_name,
                    last_name: msg.sender.last_name,
                    full_name: msg.sender.full_name
                } : null,
            };
        });
    },

    async getMessagesForAllMembers(currentMemberId: number): Promise<TransformedMessage[]> {
        const messageWithDetailsPayload = Prisma.validator<Prisma.MemberMessageDefaultArgs>()({
          include: {
            sender: { select: { member_id: true, first_name: true, last_name: true, full_name: true } },
            recipient_statuses: { 
              select: { status: true, read_at: true, recipient_member_id: true }
            },
          },
        });
        type MessageWithDetails = Prisma.MemberMessageGetPayload<typeof messageWithDetailsPayload>;

        const messagesData = await prisma.memberMessage.findMany({
            where: {
                recipient_type: 'all',
                recipient_statuses: {
                    some: {
                        recipient_member_id: currentMemberId,
                        // status: { not: 'archived' } // Opcionalno
                    },
                },
            },
            include: {
                sender: messageWithDetailsPayload.include.sender,
                recipient_statuses: {
                    where: { recipient_member_id: currentMemberId }, // Status samo za ovog lana
                    select: messageWithDetailsPayload.include.recipient_statuses.select
                }
            },
            orderBy: { created_at: 'desc' },
        });

        return (messagesData as MessageWithDetails[]).map((msg): TransformedMessage => {
            const recipientStatus = msg.recipient_statuses.find(rs => rs.recipient_member_id === currentMemberId) || msg.recipient_statuses[0];
            return {
                message_id: msg.message_id,
                member_id: msg.member_id,
                message_text: msg.message_text,
                created_at: msg.created_at,
                sender_id: msg.sender_id,
                recipient_id: msg.recipient_id,
                recipient_type: msg.recipient_type,
                sender_type: msg.sender_type,
                currentUserStatus: recipientStatus?.status as ('unread' | 'read' | 'archived' | undefined),
                currentUserReadAt: recipientStatus?.read_at,
                sender: msg.sender ? {
                    member_id: msg.sender.member_id,
                    first_name: msg.sender.first_name,
                    last_name: msg.sender.last_name,
                    full_name: msg.sender.full_name
                } : null,
            };
        });
    },

    async markAsRead(messageId: number, memberId: number): Promise<void> {
        await prisma.messageRecipientStatus.updateMany({
            where: {
                message_id: messageId,
                recipient_member_id: memberId,
            },
            data: {
                status: 'read',
                read_at: getCurrentDate(),
            },
        });
    },

    async archiveMessage(messageId: number, memberId: number): Promise<void> {
        await prisma.messageRecipientStatus.updateMany({
            where: {
                message_id: messageId,
                recipient_member_id: memberId,
            },
            data: {
                status: 'archived',
                // Moda ne elimo postaviti read_at za arhiviranje, ovisno o logici
            },
        });
    },

    async deleteMessage(messageId: number): Promise<void> {
        // Prvo obrii povezane statuse primatelja da se izbjegnu problemi s referencijalnim integritetom
        await prisma.messageRecipientStatus.deleteMany({
            where: { message_id: messageId },
        });
        await prisma.memberMessage.delete({ where: { message_id: messageId }});
    },

    async deleteAllMessages(): Promise<void> {
        await prisma.messageRecipientStatus.deleteMany({}); // Obrii sve statuse
        await prisma.memberMessage.deleteMany({}); // Zatim obrii sve poruke
    },

    async messageExists(messageId: number): Promise<boolean> {
        const count = await prisma.memberMessage.count({ where: { message_id: messageId }});
        return count > 0;
    },

    // OPTIMIZIRANA funkcija za brojanje neproitanih poruka - serverless friendly
    async countUnreadMessages(memberId: number): Promise<number> {
        try {
            // Optimiziran upit s minimalnim JOIN-om za serverless performanse
            const count = await prisma.messageRecipientStatus.count({
                where: {
                    recipient_member_id: memberId,
                    status: 'unread',
                    // Optimiziran pristup - direktno iskljuujemo self-sent poruke
                    message: {
                        sender_id: { not: memberId }
                    }
                },
            });
            return count;
        } catch (error) {
            console.error('Error counting unread messages for member', memberId, ':', error);
            return 0; // Fallback vrijednost umjesto error-a
        }
    }
};

export default memberMessageRepository;

import express from 'express';
import { fileURLToPath } from 'url';
import path from 'path';
import fs from 'fs';
import { promises as fsPromises } from 'fs';
import util from 'util';
import { exec } from 'child_process';
import { authMiddleware, roles } from '../middleware/authMiddleware.js';

// Import db
import prisma from '../utils/prisma.js';
import { Member, MembershipDetails, MembershipPeriod, StampInventory, Activity, ActivityParticipation } from '@prisma/client';

// Import member service

// Import membership service

import { getCurrentDate } from '../utils/dateUtils.js';

const execPromise = util.promisify(exec);

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const router = express.Router();

// Debug endpoint to check if file exists
router.get('/file-exists/:subdirectory/:filename', async (req, res) => {
  try {
    const { subdirectory, filename } = req.params;
    const filePath = path.join(__dirname, '..', '..', 'uploads', subdirectory, filename);

    console.log('Checking if file exists:', filePath);

    try {
      const stats = await fsPromises.stat(filePath);
      res.json({
        exists: true,
        size: stats.size,
        isFile: stats.isFile(),
        lastModified: stats.mtime
      });
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error';
      res.status(404).json({
        exists: false,
        error: errorMessage,
        checkedPath: filePath
      });
    }
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    res.status(500).json({ error: errorMessage });
  }
});

// Define file info type
interface FileInfo {
  path: string;
  size: number;
  modified: Date;
}

// Define path info types
interface PathInfoBase {
  name: string;
  path: string;
  exists: boolean;
  canWrite: boolean;
}

interface PathInfoSuccess extends PathInfoBase {
  exists: true;
  isDirectory: boolean;
  permissions: string;
  writeTest?: string;
}

interface PathInfoError extends PathInfoBase {
  exists: false;
  error: string;
}

type PathInfo = PathInfoSuccess | PathInfoError;

// List all files in uploads directory and subdirectories
router.get('/fs/list-files', async (req, res) => {
  try {
    const basePath = process.env.NODE_ENV === 'production' 
      ? '/app/uploads' 
      : path.join(__dirname, '..', '..', 'uploads');

    // Function to recursively list files with their sizes
    async function listFilesRecursive(dir: string, baseDir = ''): Promise<FileInfo[]> {
      const files = await fsPromises.readdir(dir, { withFileTypes: true });
      let results: FileInfo[] = [];

      for (const file of files) {
        const fullPath = path.join(dir, file.name);
        const relativePath = path.join(baseDir, file.name);

        if (file.isDirectory()) {
          const subResults = await listFilesRecursive(fullPath, relativePath);
          results = [...results, ...subResults];
        } else {
          const stats = await fsPromises.stat(fullPath);
          results.push({
            path: relativePath,
            size: stats.size,
            modified: stats.mtime
          });
        }
      }

      return results;
    }

    // Create base directory if it doesn't exist
    await fsPromises.mkdir(basePath, { recursive: true });

    // List all files recursively
    const fileList = await listFilesRecursive(basePath);

    // Get disk usage information
    const { stdout: diskUsage } = await execPromise('df -h');

    // Check directory permissions
    const { stdout: permissions } = await execPromise(`ls -la ${basePath}`);

    res.json({
      basePath,
      files: fileList,
      diskUsage: diskUsage.trim().split('\n'),
      permissions: permissions.trim().split('\n'),
      fileCount: fileList.length,
      totalSize: fileList.reduce((sum: number, file: FileInfo) => sum + file.size, 0)
    });
  } catch (error) {
    console.error('Error listing files:', error);
    res.status(500).json({ 
      error: error instanceof Error ? error.message : 'Unknown error',
      stack: error instanceof Error ? error.stack : undefined
    });
  }
});

// Test writing a file and reading it back
router.post('/fs/test-write', async (req, res) => {
  try {
    const basePath = process.env.NODE_ENV === 'production' 
      ? '/app/uploads' 
      : path.join(__dirname, '..', '..', 'uploads');

    const testFileName = `test-file-${Date.now()}.txt`;
    const testFilePath = path.join(basePath, testFileName);
    const content = `Test file created at ${getCurrentDate().toISOString()}`;

    // Ensure directory exists
    await fsPromises.mkdir(basePath, { recursive: true });

    // Write test file
    await fsPromises.writeFile(testFilePath, content, 'utf8');
    console.log(`Test file written to ${testFilePath}`);

    // Read it back
    const readContent = await fsPromises.readFile(testFilePath, 'utf8');

    // Get file stats
    const stats = await fsPromises.stat(testFilePath);

    res.json({
      success: true,
      file: {
        name: testFileName,
        path: testFilePath,
        size: stats.size,
        created: stats.birthtime,
        modified: stats.mtime,
        contentMatches: readContent === content,
        content: readContent
      }
    });
  } catch (error) {
    console.error('Error in file write test:', error);
    res.status(500).json({ 
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      stack: error instanceof Error ? error.stack : undefined
    });
  }
});

// Test all paths involved in image uploads
router.get('/fs/image-paths', async (req, res) => {
  try {
    const basePath = process.env.NODE_ENV === 'production' 
      ? '/app/uploads' 
      : path.join(__dirname, '..', '..', 'uploads');

    const profileImagesDir = path.join(basePath, 'profile_images');

    // Information about all relevant directories
    const pathInfo = [
      { name: 'Base uploads', path: basePath },
      { name: 'Profile images', path: profileImagesDir },
    ];

    // Check all paths
    const results: PathInfo[] = [];
    for (const info of pathInfo) {
      try {
        await fsPromises.mkdir(info.path, { recursive: true });
        const stats = await fsPromises.stat(info.path);
        const { stdout } = await execPromise(`ls -la "${info.path}"`);

        results.push({
          ...info,
          exists: true,
          isDirectory: stats.isDirectory(),
          permissions: stdout.trim().split('\n')[0],
          canWrite: true
        });
      } catch (error) {
        results.push({
          ...info,
          exists: false,
          error: error instanceof Error ? error.message : String(error),
          canWrite: false
        });
      }
    }

    // Test writing to each directory
    for (const result of results) {
      if (result.exists && 'isDirectory' in result && result.isDirectory) {
        try {
          const testFile = path.join(result.path, `test-${Date.now()}.txt`);
          await fsPromises.writeFile(testFile, 'test', 'utf8');
          await fsPromises.unlink(testFile);
          (result as PathInfoSuccess).writeTest = 'success';
        } catch (error) {
          (result as PathInfoSuccess).writeTest = `failed: ${error instanceof Error ? error.message : String(error)}`;
          result.canWrite = false;
        }
      }
    }

    res.json({
      paths: results,
      summary: {
        allPathsExist: results.every(r => r.exists),
        allPathsWritable: results.every(r => r.canWrite),
      }
    });
  } catch (error) {
    console.error('Error checking image paths:', error);
    res.status(500).json({ 
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

