router.post('/reset-test-database', async (req, res) => {
  // Dozvoljavamo resetiranje baze samo u development okruenju
  if (process.env.NODE_ENV !== 'development') {
    return res.status(403).json({ 
      error: 'Resetiranje baze podataka je doputeno samo u razvojnom okruenju' 
    });
  }

  try {
    console.log(' Resetiranje testne baze podataka...');

    // Spremamo podatke prije resetiranja
    let backupData: { 
      timestamp?: string; 
      filename?: string;
      members?: Member[];
      membership_details?: MembershipDetails[];
      membership_periods?: MembershipPeriod[];
      stamp_inventory?: StampInventory[];
      activities?: Activity[];
      activity_participants?: ActivityParticipation[];
    } = {};

    try {
      // Dohvaamo stanje kljunih tablica prije resetiranja
      const membersResult = await prisma.member.findMany();
      const membershipDetailsResult = await prisma.membershipDetails.findMany();
      const membershipPeriodsResult = await prisma.membershipPeriod.findMany();
      const stampInventoryResult = await prisma.stampInventory.findMany();
      const activitiesResult = await prisma.activity.findMany();
      const activityParticipantsResult = await prisma.activityParticipation.findMany();

      // Spremamo u backup objekt
      backupData = {
        timestamp: getCurrentDate().toISOString(),
        members: membersResult,
        membership_details: membershipDetailsResult,
        membership_periods: membershipPeriodsResult,
        stamp_inventory: stampInventoryResult,
        activities: activitiesResult,
        activity_participants: activityParticipantsResult
      };

      // Spaavamo backup u datoteku
      const backupDir = path.join(__dirname, '..', '..', 'backups');
      await fsPromises.mkdir(backupDir, { recursive: true });

      const backupFileName = `backup_${getCurrentDate().toISOString().replace(/[:.]/g, '-')}.json`;
      const backupPath = path.join(backupDir, backupFileName);

      await fsPromises.writeFile(
        backupPath, 
        JSON.stringify(backupData, null, 2), 
        'utf8'
      );

      console.log(` Backup podataka spremljen u: ${backupPath}`);
    } catch (backupError) {
      console.error(' Greka prilikom stvaranja backupa:', backupError);
      // Nastavljamo s resetiranjem ak i ako backup nije uspio
    }

    // Koristimo Prisma transakciju za sigurno izvoenje svih izmjena
    await prisma.$transaction(async (tx) => {
      // 1. RESETIRANJE KORISNIKA I LANSTVA

      // Vraanje statusa svih lanova na "registered" (poetni status)
      await tx.member.updateMany({
        data: {
          status: 'registered'
        }
      });

      // 2. RESETIRANJE LANARINA I PERIODA

      // Vraanje lanarina na trenutnu godinu i ponitavanje datuma plaanja
      const currentYear = getCurrentDate().getFullYear();
      await tx.membershipDetails.updateMany({
        data: {
          fee_payment_year: currentYear,
          fee_payment_date: null,
          card_stamp_issued: false,
          next_year_stamp_issued: false
        }
      });

      // 3. RESETIRANJE INVENTARA I MARKICA

      // Vraanje inventara markica na poetno stanje
      await tx.stampInventory.updateMany({
        data: {
          issued_count: 0
        }
      });

      // 6. RESETIRANJE PORUKA

      // Brisanje testnih poruka (zadnji dan)
      const oneDayAgo = new Date();
      oneDayAgo.setDate(oneDayAgo.getDate() - 1);

      await tx.memberMessage.deleteMany({
        where: {
          created_at: {
            gt: oneDayAgo
          }
        }
      });

      // 8. RESETIRANJE UPDATE QUEUE-a

      // Brisanje svih stavki iz reda ekanja za auriranje lozinki
      await tx.password_update_queue.deleteMany({});

      console.log(' Baza podataka uspjeno resetirana na poetno stanje');
    }, {
      timeout: 12000 // 12 sekundi - ispod Prisma Accelerate limita
    });

    // Vraamo uspjean odgovor s informacijom o backup-u
    res.json({ 
      success: true, 
      message: 'Testna baza podataka uspjeno resetirana',
      backupCreated: backupData.timestamp ? true : false,
      backupTimestamp: backupData.timestamp || null,
      timestamp: getCurrentDate()
    });
  } catch (error) {
    console.error(' Greka prilikom resetiranja baze:', error);
    res.status(500).json({ 
      error: error instanceof Error ? error.message : 'Nepoznata greka',
      timestamp: getCurrentDate()
    });
  }
});

// Endpoint za vraanje podataka iz backup-a
router.post('/restore-from-backup/:filename', async (req, res) => {
  // Dozvoljavamo vraanje backupa samo u development okruenju
  if (process.env.NODE_ENV !== 'development') {
    return res.status(403).json({ 
      error: 'Vraanje backupa je doputeno samo u razvojnom okruenju' 
    });
  }

  try {
    const { filename } = req.params;
    const backupDir = path.join(__dirname, '..', '..', 'backups');
    const backupPath = path.join(backupDir, filename);

    console.log(` Pokuaj vraanja podataka iz backupa: ${backupPath}`);

    // Provjera postojanja datoteke
    try {
      await fsPromises.access(backupPath, fs.constants.F_OK);
    } catch (_err) {
      return res.status(404).json({ 
        error: `Backup datoteka nije pronaena: ${filename}` 
      });
    }

    // Uitavanje backup podataka
    const backupData = JSON.parse(
      await fsPromises.readFile(backupPath, 'utf8')
    );

    // Provjera strukture
    if (!backupData.members || !backupData.membership_details) {
      return res.status(400).json({ 
        error: 'Nevaea struktura backup datoteke' 
      });
    }

    // Vraanje podataka u transakciji
    await prisma.$transaction(async (tx) => {
      // Primjer vraanja podataka za members tablicu
      // Ovo je kompleksno jer treba paljivo obraditi svaki redak i polje
      // Ovdje je pojednostavljeni pristup za members tablicu

      console.log(' Vraanje podataka za lanove...');

      // Prvo brisanje podataka iz tablica koje imaju strane kljueve
      await tx.password_update_queue.deleteMany({});
      await tx.activityParticipation.deleteMany({});
      await tx.annualStatistics.deleteMany({});
      await tx.auditLog.deleteMany({});
      await tx.memberMessage.deleteMany({});
      await tx.memberPermissions.deleteMany({});

      // Brisanje postojeih podataka iz membership_details (foreign key constraint)
      await tx.membershipDetails.deleteMany({});

      // Brisanje postojeih podataka iz members
      await tx.member.deleteMany({});

      // Inserti za members tablicu
      for (const member of backupData.members) {
        const { member_id: _member_id, ...memberData } = member;

        await tx.member.create({
          data: {
            ...memberData
          }
        });
      }

      // Inserti za membership_details tablicu
      for (const detail of backupData.membership_details) {
        const { detail_id: _detail_id, ...detailData } = detail;

        await tx.membershipDetails.create({
          data: {
            ...detailData
          }
        });
      }

      // Slino za ostale tablice...

      console.log(' Podaci uspjeno vraeni iz backupa');
    }, {
      timeout: 12000 // 12 sekundi - ispod Prisma Accelerate limita od 15s
    });

    res.json({
      success: true,
      message: 'Podaci uspjeno vraeni iz backupa',
      backupInfo: {
        filename,
        timestamp: backupData.timestamp,
        tables: Object.keys(backupData)
      },
      timestamp: getCurrentDate()
    });
  } catch (error) {
    console.error(' Greka prilikom vraanja backupa:', error);
    res.status(500).json({ 
      error: error instanceof Error ? error.message : 'Nepoznata greka',
      timestamp: getCurrentDate()
    });
  }
});

