/**
 * Skripta za ispravak podataka u tablici ActivityParticipation:
 * - Ispravlja formate start_time i end_time da budu pravi datumi umjesto brojeva
 * - Postavlja recognition_override na 100% za sve zapise gdje je null
 */
async function fixActivityParticipationData() {
  try {
    console.log('Zapoinjem ispravak podataka u tablici ActivityParticipation...');

    // 1. Dohvati sve zapise sudjelovanja
    const participations = await prisma.activityParticipation.findMany();
    console.log(`Pronaeno ${participations.length} zapisa sudjelovanja za obradu.`);

    // 2. Za svaki zapis, dohvati aktivnost i ispravno postavi start_time i end_time
    for (const participation of participations) {
      const activity = await prisma.activity.findUnique({
        where: { activity_id: participation.activity_id }
      });

      if (!activity) {
        console.log(`Aktivnost ${participation.activity_id} nije pronaena. Preskaem...`);
        continue;
      }

      console.log(`Obraujem sudjelovanje ID: ${participation.participation_id}`);

      // 3. Pripremamo podatke za auriranje
      const updateData: any = {
        recognition_override: 100 // Postavljamo na 100% za sve
      };

      // Ako aktivnost ima stvarne datume, koristimo njih
      if (activity.actual_start_time) {
        updateData.start_time = new Date(activity.actual_start_time);
      } else if (activity.start_date) {
        // Ako nema stvarnog poetka, koristimo planirani datum
        updateData.start_time = new Date(activity.start_date);
      }

      if (activity.actual_end_time) {
        updateData.end_time = new Date(activity.actual_end_time);
      }

      // 4. Auriramo zapis u bazi
      await prisma.activityParticipation.update({
        where: { participation_id: participation.participation_id },
        data: updateData
      });

      console.log(`Uspjeno aurirano sudjelovanje ID: ${participation.participation_id}`);
    }

    console.log('Migracija je uspjeno zavrena!');
  } catch (error) {
    console.error('Dolo je do greke prilikom migracije:', error);
  } finally {
    await prisma.$disconnect();
  }
}

// Pokreni skriptu
fixActivityParticipationData()
  .then(() => console.log('Skripta je zavrila s izvravanjem.'))
  .catch((e) => console.error('Neoekivana greka:', e));

