// Skripta za vraanje sigurnosne kopije baze podataka
import { PrismaClient } from '@prisma/client';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';
import dotenv from 'dotenv';
import readline from 'readline';

// Uitaj .env varijable
dotenv.config();

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const prisma = new PrismaClient();

// Funkcija za itanje korisnikog unosa
function askQuestion(query: string): Promise<string> {
  const rl = readline.createInterface({
    input: process.stdin,
    output: process.stdout,
  });

  return new Promise(resolve => rl.question(query, ans => {
    rl.close();
    resolve(ans);
  }));
}

// Funkcija za prikaz dostupnih sigurnosnih kopija
async function listBackups() {
  const backupDir = path.join(__dirname, '..', '..', 'backups');

  try {
    await fs.access(backupDir);
  } catch (error) {
    console.log('Direktorij za sigurnosne kopije ne postoji.');
    return [];
  }

  const files = await fs.readdir(backupDir);
  const jsonFiles = files.filter(file => file.endsWith('.json'));

  if (jsonFiles.length === 0) {
    console.log('Nema dostupnih sigurnosnih kopija.');
    return [];
  }

  console.log('Dostupne sigurnosne kopije:');
  jsonFiles.forEach((file, index) => {
    console.log(`${index + 1}. ${file}`);
  });

  return jsonFiles.map(file => path.join(backupDir, file));
}

// Funkcija za vraanje sigurnosne kopije iz JSON datoteke
async function restoreFromJson(backupFilePath: string): Promise<boolean> {
  try {
    console.log(`Uitavanje sigurnosne kopije iz: ${backupFilePath}`);

    // Provjeri postoji li datoteka
    try {
      await fs.access(backupFilePath);
    } catch (error) {
      throw new Error(`Datoteka sigurnosne kopije ne postoji: ${backupFilePath}`);
    }

    // Uitaj sadraj datoteke
    const backupContent = await fs.readFile(backupFilePath, 'utf8');
    const backup: { timestamp: string; data: Record<string, any[]> } = JSON.parse(backupContent);

    if (!backup.data) {
      throw new Error('Nevaei format sigurnosne kopije: nedostaje polje "data"');
    }

    console.log(`Sigurnosna kopija kreirana: ${backup.timestamp}`);

    // Potvrda korisnika prije brisanja podataka
    const answer = await askQuestion(
      'UPOZORENJE: Vraanje sigurnosne kopije e obrisati sve postojee podatke u bazi. elite li nastaviti? (da/ne): '
    );

    if (answer.toLowerCase() !== 'da') {
      console.log('Vraanje sigurnosne kopije prekinuto.');
      return false;
    }

    // Zaponi transakciju
    console.log('Zapoinjem vraanje sigurnosne kopije...');

    await prisma.$transaction(async (tx) => {
      // Obrii postojee podatke (obrnutim redoslijedom zbog stranih kljueva)
      console.log('Briem postojee podatke...');

      // Brisanje podataka obrnutim redoslijedom od stvaranja (zbog stranih kljueva)
      await tx.refresh_tokens.deleteMany({});
      await tx.memberPermissions.deleteMany({});
      await tx.auditLog.deleteMany({});
      await tx.annualStatistics.deleteMany({});
      await tx.cardNumber.deleteMany({});
      await tx.stampInventory.deleteMany({});
      await tx.messageRecipientStatus.deleteMany({});
      await tx.memberMessage.deleteMany({});
      await tx.membershipPeriod.deleteMany({});
      await tx.membershipDetails.deleteMany({});
      await tx.activityParticipation.deleteMany({});
      await tx.activity.deleteMany({});
      await tx.activityType.deleteMany({});
      await tx.member.deleteMany({});

      // Zadri sistemske postavke ako postoje
      const existingSettings = await tx.systemSettings.findFirst({
        where: { id: 'default' }
      });

      if (!existingSettings && backup.data.systemSettings && backup.data.systemSettings.length > 0) {
        await tx.systemSettings.create({
          data: backup.data.systemSettings[0]
        });
      }

      console.log('Postojei podaci obrisani.');

      // Vraanje podataka
      console.log('Vraam podatke iz sigurnosne kopije...');

      // Vraanje lanova
      if (backup.data.members && backup.data.members.length > 0) {
        for (const member of backup.data.members) {
          await tx.member.create({
            data: {
              ...member,
              // Izbjegavamo probleme s datumima pretvarajui ih u Date objekte
              created_at: member.created_at ? new Date(member.created_at) : undefined,
              updated_at: member.updated_at ? new Date(member.updated_at) : undefined,
              date_of_birth: member.date_of_birth ? new Date(member.date_of_birth) : undefined,
              last_login: member.last_login ? new Date(member.last_login) : undefined,
              profile_image_updated_at: member.profile_image_updated_at ? new Date(member.profile_image_updated_at) : undefined,
              locked_until: member.locked_until ? new Date(member.locked_until) : undefined,
              last_failed_login: member.last_failed_login ? new Date(member.last_failed_login) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.members.length} lanova`);
      }

      // Vraanje tipova aktivnosti
      if (backup.data.activityTypes && backup.data.activityTypes.length > 0) {
        for (const activityType of backup.data.activityTypes) {
          await tx.activityType.create({
            data: {
              ...activityType,
              created_at: activityType.created_at ? new Date(activityType.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.activityTypes.length} tipova aktivnosti`);
      }

      // Vraanje aktivnosti
      if (backup.data.activities && backup.data.activities.length > 0) {
        for (const activity of backup.data.activities) {
          await tx.activity.create({
            data: {
              ...activity,
              start_date: activity.start_date ? new Date(activity.start_date) : undefined,
              end_date: activity.end_date ? new Date(activity.end_date) : undefined,
              created_at: activity.created_at ? new Date(activity.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.activities.length} aktivnosti`);
      }

      // Vraanje sudionika aktivnosti
      if (backup.data.activityParticipants && backup.data.activityParticipants.length > 0) {
        for (const participant of backup.data.activityParticipants) {
          await tx.activityParticipation.create({ data: participant as any }); // ISPRAVAK
        }
        console.log(`Vraeno ${backup.data.activityParticipants.length} sudionika aktivnosti`);
      }

      // Vraanje detalja lanstva
      if (backup.data.membershipDetails && backup.data.membershipDetails.length > 0) {
        for (const details of backup.data.membershipDetails) {
          await tx.membershipDetails.create({
            data: {
              ...details,
              fee_payment_date: details.fee_payment_date ? new Date(details.fee_payment_date) : undefined,
              active_until: details.active_until ? new Date(details.active_until) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.membershipDetails.length} detalja lanstva`);
      }

      // Vraanje perioda lanstva
      if (backup.data.membershipPeriods && backup.data.membershipPeriods.length > 0) {
        for (const period of backup.data.membershipPeriods) {
          await tx.membershipPeriod.create({
            data: {
              ...period,
              start_date: period.start_date ? new Date(period.start_date) : undefined,
              end_date: period.end_date ? new Date(period.end_date) : undefined,
              created_at: period.created_at ? new Date(period.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.membershipPeriods.length} perioda lanstva`);
      }

      // Vraanje poruka lanova
      if (backup.data.memberMessages && backup.data.memberMessages.length > 0) {
        for (const message of backup.data.memberMessages) {
          await tx.memberMessage.create({
            data: {
              ...message,
              created_at: message.created_at ? new Date(message.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.memberMessages.length} poruka lanova`);
      }

      // Vraanje statusa primatelja poruka
      if (backup.data.messageRecipientStatuses && backup.data.messageRecipientStatuses.length > 0) {
        for (const status of backup.data.messageRecipientStatuses) {
          await tx.messageRecipientStatus.create({
            data: {
              ...status,
              created_at: status.created_at ? new Date(status.created_at) : undefined,
              read_at: status.read_at ? new Date(status.read_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.messageRecipientStatuses.length} statusa primatelja poruka`);
      }

      // Vraanje inventara markica
      if (backup.data.stampInventory && backup.data.stampInventory.length > 0) {
        for (const inventory of backup.data.stampInventory) {
          await tx.stampInventory.create({
            data: {
              ...inventory,
              last_updated: inventory.last_updated ? new Date(inventory.last_updated) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.stampInventory.length} inventara markica`);
      }

      // Vraanje brojeva iskaznica
      if (backup.data.cardNumbers && backup.data.cardNumbers.length > 0) {
        for (const cardNumber of backup.data.cardNumbers) {
          await tx.cardNumber.create({
            data: {
              ...cardNumber,
              created_at: cardNumber.created_at ? new Date(cardNumber.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.cardNumbers.length} brojeva iskaznica`);
      }

      // Vraanje potroenih brojeva kartica
      if (backup.data.consumedCardNumbers && backup.data.consumedCardNumbers.length > 0) {
        for (const consumed of backup.data.consumedCardNumbers) {
          await tx.consumedCardNumber.create({
            data: {
              ...consumed,
              consumed_at: consumed.consumed_at ? new Date(consumed.consumed_at) : undefined,
              issued_at: consumed.issued_at ? new Date(consumed.issued_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.consumedCardNumbers.length} potroenih brojeva kartica`);
      }

      // Vraanje queue-a za promjenu lozinke
      if (backup.data.passwordUpdateQueue && backup.data.passwordUpdateQueue.length > 0) {
        for (const queueItem of backup.data.passwordUpdateQueue) {
          await tx.password_update_queue.create({
            data: {
              ...queueItem,
              created_at: queueItem.created_at ? new Date(queueItem.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.passwordUpdateQueue.length} zahtjeva za promjenu lozinke`);
      }

      // Vraanje godinjih statistika
      if (backup.data.annualStatistics && backup.data.annualStatistics.length > 0) {
        for (const statistics of backup.data.annualStatistics) {
          await tx.annualStatistics.create({
            data: {
              ...statistics,
              created_at: statistics.created_at ? new Date(statistics.created_at) : undefined,
              updated_at: statistics.updated_at ? new Date(statistics.updated_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.annualStatistics.length} godinjih statistika`);
      }

      // Vraanje audit logova
      if (backup.data.auditLogs && backup.data.auditLogs.length > 0) {
        for (const log of backup.data.auditLogs) {
          await tx.auditLog.create({
            data: {
              ...log,
              created_at: log.created_at ? new Date(log.created_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.auditLogs.length} audit logova`);
      }

      // Vraanje dozvola lanova
      if (backup.data.memberPermissions && backup.data.memberPermissions.length > 0) {
        for (const permission of backup.data.memberPermissions) {
          await tx.memberPermissions.create({
            data: {
              ...permission,
              created_at: permission.created_at ? new Date(permission.created_at) : undefined,
              updated_at: permission.updated_at ? new Date(permission.updated_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.memberPermissions.length} dozvola lanova`);
      }

      // Vraanje refresh tokena
      if (backup.data.refreshTokens && backup.data.refreshTokens.length > 0) {
        for (const token of backup.data.refreshTokens) {
          await tx.refresh_tokens.create({
            data: {
              ...token,
              created_at: token.created_at ? new Date(token.created_at) : undefined,
              expires_at: token.expires_at ? new Date(token.expires_at) : undefined
            }
          });
        }
        console.log(`Vraeno ${backup.data.refreshTokens.length} refresh tokena`);
      }
    }, {
      maxWait: 60000, // 60 sekundi maksimalno ekanje
      timeout: 120000 // 120 sekundi timeout
    });

    console.log('Sigurnosna kopija uspjeno vraena!');
    return true;
  } catch (error: unknown) {
    console.error('Greka prilikom vraanja sigurnosne kopije:', error);
    return false;
  } finally {
    await prisma.$disconnect();
  }
}

// Glavna funkcija
async function main() {
  console.log('=== ALAT ZA VRAANJE SIGURNOSNE KOPIJE ===');

  const backupFiles = await listBackups();

  if (backupFiles.length === 0) {
    console.log('Nema dostupnih sigurnosnih kopija za vraanje.');
    return;
  }

  const backupIndex = await askQuestion(
    `Unesite broj sigurnosne kopije koju elite vratiti (1-${backupFiles.length}) ili 'q' za izlaz: `
  );

  if (backupIndex.toLowerCase() === 'q') {
    console.log('Vraanje sigurnosne kopije prekinuto.');
    return;
  }

  const index = parseInt(backupIndex, 10) - 1;

  if (isNaN(index) || index < 0 || index >= backupFiles.length) {
    console.log('Nevaei unos. Vraanje sigurnosne kopije prekinuto.');
    return;
  }

  const selectedBackup = backupFiles[index];
  const success = await restoreFromJson(selectedBackup);

  if (success) {
    console.log('Vraanje sigurnosne kopije uspjeno zavreno.');
  } else {
    console.log('Vraanje sigurnosne kopije nije uspjelo.');
  }
}

