// Skripta za automatsko zakazivanje sigurnosnih kopija
import backupDatabaseToJson from './backupDatabase.js';
import dotenv from 'dotenv';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

// Uitaj .env varijable
dotenv.config();

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Funkcija za zapisivanje log poruka
async function logMessage(message: string) {
  const logDir = process.env.NODE_ENV === 'production' 
    ? '/tmp/promina-drnis-logs' 
    : path.join(__dirname, '..', '..', 'logs');

  try {
    await fs.access(logDir);
  } catch (error) {
    // Direktorij ne postoji, kreiraj ga
    await fs.mkdir(logDir, { recursive: true });
  }

  const now = new Date();
  const logFile = path.join(logDir, `backup_${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}.log`);

  const timestamp = now.toISOString();
  const logEntry = `[${timestamp}] ${message}\n`;

  try {
    await fs.appendFile(logFile, logEntry);
  } catch (error) {
    console.error('Greka prilikom zapisivanja u log datoteku:', error);
  }
}

// Glavna funkcija za pokretanje sigurnosne kopije
async function runScheduledBackup() {
  try {
    await logMessage('Zapoinjem zakazanu sigurnosnu kopiju...');

    const result = await backupDatabaseToJson();

    if (result.success) {
      await logMessage(`Sigurnosna kopija uspjeno kreirana: ${result.fileName}`);
    } else {
      await logMessage(`Greka prilikom stvaranja sigurnosne kopije: ${result.error}`);
    }

    return result;
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    await logMessage(`Neoekivana greka prilikom stvaranja sigurnosne kopije: ${errorMessage}`);
    throw error;
  }
}

// Ako se skripta pokree direktno, izvri sigurnosnu kopiju
if (process.argv[1] === fileURLToPath(import.meta.url)) {
  runScheduledBackup().then(result => {
    if (result.success) {
      console.log('Zakazana sigurnosna kopija uspjeno kreirana');
      process.exit(0);
    } else {
      console.error('Greka prilikom stvaranja zakazane sigurnosne kopije');
      process.exit(1);
    }
  }).catch(error => {
    console.error('Neoekivana greka:', error);
    process.exit(1);
  });
}

export default runScheduledBackup;

