    if (!memberGender) {
        throw new Error('Member gender is required');
    }
    switch (memberGender.toLowerCase()) {
        case 'male':
            return 'male';
        case 'female':
            return 'female';
        default:
            throw new Error(`Invalid member gender: ${memberGender}`);
    }
}

