    async getMemberWithActivities(memberId: number): Promise<MemberWithActivities | null> {
        try {
            const member = await this.getMemberById(memberId);
            if (!member) return null;

            console.log(`[MEMBER] Dohvaam aktivnosti za lana ID: ${memberId}`);

            let activitiesData = [];

            try {
                const participations = await prisma.activityParticipation.findMany({
                    where: {
                        member_id: memberId
                    },
                    include: {
                        activity: {
                            select: {
                                activity_id: true,
                                name: true,
                                start_date: true,
                                actual_start_time: true,
                                actual_end_time: true
                            }
                        }
                    },
                    orderBy: {
                        activity: {
                            start_date: 'desc'
                        }
                    }
                });

                // Transformiramo Prisma rezultate u oekivani format
                activitiesData = participations.map(p => ({
                    activity_id: p.activity.activity_id,
                    name: p.activity.name,
                    date: p.activity.start_date,
                    manual_hours: p.manual_hours,
                    actual_start_time: p.activity.actual_start_time,
                    actual_end_time: p.activity.actual_end_time
                }));

                console.log(`[MEMBER] Dohvaeno ${activitiesData.length} aktivnosti za lana ${memberId}`);
            } catch (error) {
                console.error(`[MEMBER] Greka prilikom dohvaanja aktivnosti za lana ${memberId}:`, error);
                throw error;
            }

            /**
             * VANA NAPOMENA:
             * -------------------
             * Transformacija izmeu polja u bazi i API odgovora:
             * 
             * Baza podataka:
             * - U tablici activity_participations imamo kolonu manual_hours (DOUBLE PRECISION)
             * - U tablici members imamo kolonu total_hours (ukupni sati aktivnosti u minutama)
             * 
             * Frontend/API oekuje:
             * - hours_spent polje po aktivnosti (umjesto manual_hours)
             * - total_hours za ukupne sate lana
             * 
             * Transformacija:
             * 1. hours_spent je virtualno/izraunato polje za frontend koje se generira iz:
             *    - manual_hours ako postoje (prioritet)
             *    - ili iz razlike actual_start_time i actual_end_time ako nije unesen manual_hours
             * 
             * 2. total_hours se odvojeno agregira preko updateMemberTotalHours funkcije koja takoer 
             *    koristi obje metode raunanja, sekvencijalno za svaku aktivnost lana
             * 
             * Svrha: Da bi podrali i runi unos sati i automatski izraun iz vremena aktivnosti
             */

            // Procesiramo rezultate kako bi izraunali hours_spent za svaku aktivnost
            const activitiesWithHours = activitiesData.map(activity => {
                let hours_spent = null;

                // Prvo provjerimo postoji li manual_hours
                if (activity.manual_hours !== null && activity.manual_hours !== undefined) {
                    hours_spent = activity.manual_hours;
                }
                // Ako nema manual_hours, a postoje vremena poetka i kraja, izraunaj razliku
                else if (activity.actual_start_time && activity.actual_end_time) {
                    const minutesDiff = differenceInMinutes(
                        new Date(activity.actual_end_time),
                        new Date(activity.actual_start_time)
                    );
                    if (minutesDiff > 0) {
                        // Pretvori minute u sate s decimalama (npr. 90 minuta = 1.5 sati)
                        hours_spent = Number((minutesDiff / 60).toFixed(2));
                    }
                }

                return {
                    activity_id: activity.activity_id,
                    name: activity.name,
                    date: activity.date.toISOString().split('T')[0], // Konvertiraj Date u string format YYYY-MM-DD
                    hours_spent
                };
            });

            return {
                ...member,
                activities: activitiesWithHours
            };
        } catch (error: unknown) {
            const errorMessage = error instanceof Error ? error.message : String(error);
            throw new Error('Error fetching member with activities: ' + errorMessage);
        }
    },

	async getMemberWithDetails(memberId: number): Promise<Member | null> {
        try {
            const member = await memberRepository.findById(memberId);
            if (!member) return null;

            const periods = await membershipRepository.getMembershipPeriods(memberId);
            const currentPeriod = await membershipRepository.getCurrentPeriod(memberId);

            // Frontend e sam izraunati trajanje na temelju perioda
            member.membership_history = {
                periods,
                current_period: currentPeriod ?? undefined,
            };

            return member;
        } catch (error: unknown) {
            const errorMessage = error instanceof Error ? error.message : String(error);
            throw new Error('Error fetching member with details: ' + errorMessage);
        }
    },

    async getMemberAnnualStats(memberId: number): Promise<Awaited<ReturnType<typeof memberRepository.getAnnualStats>>> {
        try {
            const stats = await memberRepository.getAnnualStats(memberId);
            if (!stats) {
                throw new Error('Statistika nije pronaena za ovog lana.');
            }
            return stats;
        } catch (error: unknown) {
            const errorMessage = error instanceof Error ? error.message : String(error);
            throw new Error('Error fetching member annual statistics: ' + errorMessage);
        }
    },

    async updateMembershipFee(
        memberId: number,
        paymentDate: Date,
        req: Request,
        isRenewalPayment?: boolean
    ): Promise<void> {
        try {
            const member = await this.getMemberById(memberId);
            if (!member) {
                throw new Error("Member not found");
            }

            const paymentMonth = paymentDate.getMonth(); // 0-11 where 10=Nov, 11=Dec
            const currentYear = getCurrentDate().getFullYear();

            // Determine which year to assign the payment to
            let paymentYear = currentYear;

            // Logic for determining payment year:
            // 1. If isRenewalPayment is explicitly true, use next year (for Nov/Dec renewals)
            // 2. If payment month is Nov (10) or Dec (11), and there's no existing payment
            //    for this year, use current year (new memberships)
            // 3. If payment month is Nov (10) or Dec (11), and there is an existing
            //    current year payment, use next year (implicit renewal)

            if (isRenewalPayment) {
              // Explicit renewal flag - use next year
              paymentYear = currentYear + 1;
              console.log(`Using explicit renewal: setting payment year to ${paymentYear}`);
            } else if (paymentMonth === 10 || paymentMonth === 11) { // Nov or Dec
              const existingYear = member?.membership_details?.fee_payment_year;

              // Check if this is potentially a renewal
              if (existingYear && existingYear >= currentYear) {
                // This is likely a renewal - assign to next year
                paymentYear = currentYear + 1;
                console.log(`Late year payment with existing current payment: setting to ${paymentYear}`);
              } else {
                // This is likely a new member or late payment for current year
                paymentYear = currentYear;
                console.log(`Late year payment for new/lapsed member: setting to ${paymentYear}`);
              }
            } else {
              // Regular payment during the year - use current year
              paymentYear = currentYear;
              console.log(`Regular payment during year: setting to ${paymentYear}`);
            }

            // Fix type issue - Convert the Date to ISO string for storage
            const details = {
              fee_payment_year: paymentYear,
              fee_payment_date: paymentDate, // Pass the Date object directly, not a string
            };

            await membershipRepository.updateMembershipDetails(memberId, details);

            // Call the newly added method
            await this.updateMembershipPeriodIfNeeded(memberId, paymentDate);

            if (req.user?.id) {
              await auditService.logAction(
                'MEMBERSHIP_FEE_PAYMENT',
                req.user?.id || null, // Koristimo ID iz req.user
                `Membership fee paid for ${paymentYear}`,
                req,
                'success',
                memberId,
                req.user?.performer_type
              );
            }
        } catch (error) {
            console.error("Service error:", error);
            throw error instanceof Error ? error : new Error(String(error));
        }
    },

    // Add the missing method
    async updateMembershipPeriodIfNeeded(memberId: number, paymentDate: Date): Promise<void> {
        try {
            // Get the latest membership period for this member
            const periods = await membershipRepository.getMembershipPeriods(memberId);
            const currentPeriod = periods.find(p => !p.end_date);

            // If no active period exists, create a new one starting from the payment date
            if (!currentPeriod) {
                await membershipRepository.createMembershipPeriod(memberId, paymentDate);
                console.log(`Created new membership period for member ${memberId}`);
            } else {
                console.log(`Active membership period exists for member ${memberId}, no need to create new one`);
            }
        } catch (error) {
            console.error("Error updating membership period:", error);
            throw error;
        }
    },

    async updateMembershipCard(
        memberId: number, 
        cardNumber: string, 
        stampIssued: boolean
    ): Promise<void> {
        try {
            await membershipService.updateCardDetails(memberId, cardNumber, stampIssued);
        } catch (error: unknown) {
            const errorMessage = error instanceof Error ? error.message : String(error);
            throw new Error('Error updating card details: ' + errorMessage);
        }
    },

    async getMemberWithCardDetails(memberId: number) {
        console.log(`[MEMBER] Dohvaam detalje lana s karticom za ID: ${memberId}`);

        try {
            const memberWithCard = await prisma.member.findUnique({
                where: {
                    member_id: memberId
                },
                include: {
                    membership_details: {
                        select: {
                            card_number: true,
                            card_stamp_issued: true,
                            fee_payment_date: true
                        }
                    }
                }
            });

            if (!memberWithCard) {
                console.log(`[MEMBER] lan s ID ${memberId} nije pronaen`);
                return { rows: [] };
            }

            const { membership_details: _omit, ...memberWithoutDetails } = memberWithCard as typeof memberWithCard & { membership_details?: unknown };
            const result = {
                ...memberWithoutDetails,
                card_number: memberWithCard.membership_details?.card_number || null,
                card_stamp_issued: memberWithCard.membership_details?.card_stamp_issued || null,
                fee_payment_date: memberWithCard.membership_details?.fee_payment_date || null
            };

            console.log(`[MEMBER] Uspjeno dohvaeni detalji lana ${memberId} s karticom`);

            return { rows: [result] };
        } catch (error) {
            console.error(`[MEMBER] Greka prilikom dohvaanja detalja lana ${memberId}:`, error);
            throw error;
        }
    }
};

export default memberService;

// src/services/membership.service.ts
import membershipRepository from "../repositories/membership.repository.js";
import { 
  MembershipDetails,
  MembershipPeriod,
  MembershipEndReason,
} from "../shared/types/membership.js";
import { Member } from "../shared/types/member.js";
import memberRepository from "../repositories/member.repository.js";
import auditService from "./audit.service.js";
import { Request } from "express";
import prisma from "../utils/prisma.js";
import { Prisma } from "@prisma/client";
import { parseDate, getCurrentDate, formatDate } from '../utils/dateUtils.js';
import { updateAnnualStatistics } from './statistics.service.js';
import { PerformerType } from "@prisma/client";

// Definicija tipa za tijelo zahtjeva za auriranje lanstva
interface MembershipUpdatePayload {
  paymentDate?: string;
  cardNumber?: string;
  stampIssued?: boolean;
  isRenewalPayment?: boolean;
}

const membershipService = {
  async processFeePayment(
    memberId: number,
    paymentDate: Date,
    performerId?: number,
    isRenewalPayment?: boolean,
    performerType?: PerformerType
  ): Promise<Member | null> {
    try {
      // Get system settings
      const settings = await prisma.systemSettings.findFirst({
        where: { id: "default" },
      });

      const renewalStartDay = settings?.renewalStartDay || 31;

      // Koristimo direktno Date objekt umjesto parsiranja, jer ve imamo Date
      const validPaymentDate = new Date(paymentDate);
      validPaymentDate.setHours(12, 0, 0, 0); // Standardize time // Standardize time

      const member = await memberRepository.findById(memberId);
      if (!member) {
        throw new Error("Member not found");
      }

      const paymentYear = paymentDate.getFullYear();
      const paymentMonth = paymentDate.getMonth();
      const paymentDay = paymentDate.getDate();

      // Create cutoff date (October 31st of current year)
      const cutoffDate = new Date(paymentYear, 9, renewalStartDay); // Month 9 is October
      let startDate = validPaymentDate; // Koristi stvarni datum uplate kao poetni datum

      // Provjeri je li ovo novi lan (koji nikad nije platio lanarinu)
      // ili postojei lan koji obnavlja lanstvo
      const isNewMember = !member.membership_details?.fee_payment_date;

      // Ispii vane informacije za dijagnostiku
      console.log(`Processing payment for member ${memberId}:`, {
        isNewMember,
        paymentDate: validPaymentDate,
        cutoffDate,
        isAfterCutoff: validPaymentDate > cutoffDate,
      });

      // Samo za postojee lanove koji produljuju lanstvo:
      // Ako je plaanje nakon cutoff datuma, lanstvo poinje sljedee godine
      if (validPaymentDate > cutoffDate && !isNewMember) {
        console.log(`Payment after cutoff date for EXISTING member - counting for next year`);
        startDate = new Date(paymentYear + 1, 0, 1); // Postavi na 1. sijenja sljedee godine
      } else if (validPaymentDate > cutoffDate && isNewMember) {
        console.log(`Payment after cutoff date for NEW member - still counting for current year`);
        // Za nove lanove ne mijenjamo godinu, ak i ako je kasno u godini
      }

      await prisma.$transaction(async (tx) => {
        console.log(`[MEMBERSHIP] Poinje transakcija za uplatu lanarine lana ${memberId}`);

        // Update membership details
        await tx.membershipDetails.upsert({
          where: { member_id: memberId },
          update: {
            fee_payment_year: paymentYear,
            fee_payment_date: validPaymentDate,
          },
          create: {
            member_id: memberId,
            fee_payment_year: paymentYear,
            fee_payment_date: validPaymentDate,
          }
        });
        console.log(`[MEMBERSHIP] Aurirani detalji lanstva za lana ${memberId}`);

        // Automatski postaviti status lana na "registered" kad plati lanarinu
        await tx.member.update({
          where: { member_id: memberId },
          data: { status: 'registered' }
        });
        console.log(`[MEMBERSHIP] Status lana ${memberId} postavljen na "registered" nakon plaanja lanarine`);

        // Check current period using Prisma
        const currentPeriod = await tx.membershipPeriod.findFirst({
          where: {
            member_id: memberId,
            end_date: null
          },
          orderBy: { start_date: 'desc' }
        });

        // Handle period management
        if (!currentPeriod) {
          await tx.membershipPeriod.create({
            data: {
              member_id: memberId,
              start_date: validPaymentDate
            }
          });
          console.log(`[MEMBERSHIP] Kreiran novi period lanstva za lana ${memberId}`);
        } else if (currentPeriod.end_reason === "non_payment") {
          // End current period
          await tx.membershipPeriod.update({
            where: { period_id: currentPeriod.period_id },
            data: {
              end_date: getCurrentDate(),
              end_reason: "non_payment"
            }
          });
          // Create new period
          await tx.membershipPeriod.create({
            data: {
              member_id: memberId,
              start_date: validPaymentDate
            }
          });
          console.log(`[MEMBERSHIP] Zavren stari i kreiran novi period za lana ${memberId}`);
        }

        console.log(`[MEMBERSHIP] Transakcija uspjeno zavrena za lana ${memberId}`);
      });

      if (performerId) {
        await auditService.logAction(
          "MEMBERSHIP_FEE_PAYMENT",
          performerId,
          `Membership fee paid for ${paymentYear}`,
          undefined,
          "success",
          memberId,
          performerType
        );
      }

      await updateAnnualStatistics(memberId, paymentYear);

      return await memberRepository.findById(memberId);
    } catch (error) {
      throw new Error(
        `Error processing fee payment: ${
          error instanceof Error ? error.message : "Unknown error"
        }`
      );
    }
  },

  async isMembershipActive(memberId: number): Promise<boolean> {
    try {
      const member = await memberRepository.findById(memberId);
      if (!member || !member.membership_details) {
        return false;
      }

      // Ako imamo fee_payment_year, usporedimo s trenutnom godinom
      if (member.membership_details.fee_payment_year) {
        const currentYear = getCurrentDate().getFullYear();
        return member.membership_details.fee_payment_year >= currentYear;
      }

      return false;
    } catch (error) {
      console.error("Error checking membership activity:", error);
      return false;
    }
  },

  async getMembershipDetails(
    memberId: number
  ): Promise<MembershipDetails | undefined> {
    try {
      const member = await memberRepository.findById(memberId);
      return member?.membership_details;
    } catch (error) {
      console.error("Error getting membership details:", error);
      return undefined;
    }
  },

  async updateCardDetails(memberId: number, cardNumber: string | undefined, stampIssued: boolean | null | undefined, performerId?: number): Promise<void> {
    try {
      // Prvo dohvatimo trenutni broj kartice
      const details = await membershipRepository.getMembershipDetails(memberId);
      const previousCardNumber = details?.card_number;

      // Ako je lan imao staru karticu i mijenja broj, oznai staru kao potroenu
      if (previousCardNumber && cardNumber !== undefined && previousCardNumber !== cardNumber && cardNumber.trim() !== "") {
        const cardNumberRepository = (await import('../repositories/cardnumber.repository.js')).default;
        await cardNumberRepository.markCardNumberConsumed(previousCardNumber, memberId);
      }
      console.log('==== MEMBERSHIP CARD UPDATE DETAILS ====');
      console.log(`Member ID: ${memberId}`);
      console.log(`Card number: "${cardNumber}"`);
      console.log(`Stamp issued: ${stampIssued}`);

      const member = await prisma.member.findUnique({
        where: { member_id: memberId },
        select: {
          status: true,
          registration_completed: true
        }
      });

      if (!member) {
        throw new Error(`Member with ID ${memberId} not found`);
      }

      console.log('Current member status before transaction:', member);

      await prisma.$transaction(async (tx) => {
        // Prvo auriramo status lana na 'registered' i registration_completed na true
        // ako je dodijeljen broj kartice (preslikavamo ponaanje iz updateMemberWithCardAndPassword)
        // VANO: Provjeravamo da li je cardNumber stvarno dodijeljen (nije prazan string)
        if (cardNumber !== undefined && cardNumber !== null && cardNumber.trim() !== "") {
          console.log(`Updating member status for member ${memberId} to: registered and registration_completed: true`);

          // OPTIMIZACIJA: Prisma update umjesto client.query
          const updateResult = await tx.member.update({
            where: { member_id: memberId },
            data: {
              status: 'registered',
              registration_completed: true
            },
            select: {
              member_id: true,
              status: true,
              registration_completed: true
            }
          });

          console.log('Member status update result:', updateResult);
        } else {
          console.log(`Card number is empty or undefined, not updating member status`);
        }

        // OPTIMIZACIJA: Prisma findUnique umjesto client.query
        const existingMembershipDetails = await tx.membershipDetails.findUnique({
          where: { member_id: memberId },
          select: {
            member_id: true,
            card_number: true
          }
        });

        const memberExists = !!existingMembershipDetails;
        console.log('Membership details exist:', memberExists);
        if (memberExists) {
          console.log('Current card number:', existingMembershipDetails.card_number);
        }

        // Zatim auriramo broj lanske iskaznice
        if (!memberExists) {
          // OPTIMIZACIJA: Prisma create umjesto INSERT
          console.log(`Creating new membership details for member ${memberId}, card_number: ${cardNumber}, stamp_issued: FALSE (forced)`);
          await tx.membershipDetails.create({
            data: {
              member_id: memberId,
              card_number: cardNumber || "",
              card_stamp_issued: false
            }
          });
        } else {
          // Pripremi podatke za update
          const updateData: Prisma.MembershipDetailsUpdateInput = {};

          // Ako je proslijeden broj kartice, auriramo ga
          if (cardNumber !== undefined && cardNumber !== null && cardNumber.trim() !== "") {
            console.log(`Updating card number for member ${memberId} to: ${cardNumber}`);
            updateData.card_number = cardNumber.trim();
          } else {
            console.log(`Card number is empty or undefined, not updating card number`);
          }

          // Ako je proslijeden status markice, auriramo ga
          if (stampIssued !== null && stampIssued !== undefined) {
            console.log(`Explicitly updating stamp status for member ${memberId} to: ${stampIssued}`);
            updateData.card_stamp_issued = stampIssued;
          }

          // OPTIMIZACIJA: Prisma update umjesto UPDATE query
          if (Object.keys(updateData).length > 0) {
            await tx.membershipDetails.update({
              where: { member_id: memberId },
              data: updateData
            });
          }
        }

        // OPTIMIZACIJA: Prisma deleteMany umjesto DELETE query
        if (cardNumber !== undefined && cardNumber !== null && cardNumber.trim() !== "") {
          await tx.password_update_queue.deleteMany({
            where: {
              member_id: memberId,
              card_number: { not: cardNumber.trim() }
            }
          });

          console.log('Card number updated successfully');
        }

        // OPTIMIZACIJA: Prisma findUnique umjesto SELECT query
        const memberAfterUpdate = await tx.member.findUnique({
          where: { member_id: memberId },
          select: {
            member_id: true,
            full_name: true,
            status: true,
            registration_completed: true
          }
        });

        console.log('Member after all updates:', memberAfterUpdate);
      });
    } catch (error) {
      console.error('Error updating card details:', error);
      throw new Error(`Failed to update card details: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  },

  async updateMembershipHistory(
    memberId: number,
    periods: MembershipPeriod[],
    performerId?: number,
    updateMemberStatus: boolean = false,
    performerType?: PerformerType
  ): Promise<void> {
    try {
      const member = await memberRepository.findById(memberId);
      if (!member) throw new Error("Member not found");

      await membershipRepository.updateMembershipPeriods(memberId, periods);

      // Automatsko auriranje statusa lana na temelju perioda
      if (updateMemberStatus) {
        // Provjeri postoji li aktivni period (bez end_date)
        const hasActivePeriod = periods.some(p => !p.end_date);

        if (hasActivePeriod) {
          // Koristimo Prisma umjesto memberRepository za auriranje statusa
          await prisma.member.update({
            where: { member_id: memberId },
            data: { status: 'registered' }
          });

          if (performerId) {
            await auditService.logAction(
              "UPDATE_MEMBER_HISTORY",
              performerId,
              `Updated membership history for member ${memberId}`,
              undefined,
              "success",
              memberId,
              performerType
            );
          }
        }
      }
    } catch (error) {
      console.error("Error updating membership history:", error);
      throw error;
    }
  },

  async updateMembership(
    memberId: number,
    payload: MembershipUpdatePayload,
    performerId?: number,
    performerType?: PerformerType
  ): Promise<Member | null> {
    const { paymentDate, cardNumber, stampIssued, isRenewalPayment } = payload;

    if (paymentDate) {
      await this.processFeePayment(
        memberId,
        parseDate(paymentDate),
        performerId,
        isRenewalPayment,
        performerType
      );
    }

    if (typeof cardNumber !== 'undefined' || typeof stampIssued !== 'undefined') {
        await this.updateCardDetails(memberId, cardNumber, stampIssued, performerId);
    }

    return memberRepository.findById(memberId);
  },

  async terminateMembership(
    memberId: number,
    reason: MembershipEndReason,
    performerId?: number,
    endDateStr?: string,
    performerType?: PerformerType
  ): Promise<void> {
    try {
        const endDate = endDateStr ? parseDate(endDateStr) : getCurrentDate();
        const currentPeriod = await membershipRepository.getCurrentPeriod(memberId);
        if (currentPeriod) {
            await membershipRepository.endMembershipPeriod(
                currentPeriod.period_id,
                endDate,
                reason
            );
        }

        await prisma.member.update({
          where: { member_id: memberId },
          data: { status: 'former_member' },
        });

        const details = await membershipRepository.getMembershipDetails(memberId);
        if (details?.card_number) {
            const cardNumberRepository = (await import('../repositories/cardnumber.repository.js')).default;
            await cardNumberRepository.markCardNumberConsumed(details.card_number, memberId);
        }

        if (performerId) {
          await auditService.logAction(
            'TERMINATE_MEMBERSHIP',
            performerId,
            `Terminated membership for member ${memberId} with reason: ${reason}`,
            undefined,
            'success',
            memberId,
            performerType
          );
        }
    } catch (error: unknown) {
        const errorMessage = error instanceof Error ? error.message : String(error);
        throw new Error('Error terminating membership: ' + errorMessage);
    }
  },

  async updateMembershipEndReason(
    memberId: number,
    periodId: number,
    endReason: MembershipEndReason,
    performerId?: number,
    performerType?: PerformerType
  ): Promise<void> {
    try {
      const member = await memberRepository.findById(memberId);
      if (!member) throw new Error("Member not found");

      const periods = await membershipRepository.getMembershipPeriods(memberId);
      const periodToUpdate = periods.find(p => p.period_id === periodId);

      if (!periodToUpdate) {
        throw new Error("Membership period not found");
      }

      await membershipRepository.updatePeriodEndReason(periodId, endReason);

      if (performerId) {
        await auditService.logAction(
          'UPDATE_MEMBERSHIP_END_REASON',
          performerId,
          `Updated end reason for period ${periodId} for member ${memberId}`,
          undefined,
          'success',
          memberId,
          performerType
        );
      }
    } catch (error) {
      console.error("Error updating membership period end reason:", error);
      throw error;
    }
  },


  async getMembershipHistory(memberId: number, req?: Request): Promise<{
    periods: MembershipPeriod[];
    totalDuration: string;
    currentPeriod?: MembershipPeriod;
  }> {
    const periods = await membershipRepository.getMembershipPeriods(memberId);
    const currentPeriod = periods.find((p: MembershipPeriod) => !p.end_date);

    // Calculate total duration
    let totalDays = 0;
    periods.forEach((period: MembershipPeriod) => {
      const end = period.end_date ? parseDate(period.end_date) : getCurrentDate();
      const start = parseDate(period.start_date);
      totalDays += Math.floor(
        (end.getTime() - start.getTime()) / (1000 * 60 * 60 * 24)
      );
    });

    const years = Math.floor(totalDays / 365);
    const months = Math.floor((totalDays % 365) / 30);
    const days = totalDays % 30;

    return {
      periods,
      totalDuration: `${years} years, ${months} months, ${days} days`,
      currentPeriod,
    };
  },

  async checkAutoTerminations(): Promise<void> {
    try {
      const currentYear = getCurrentDate().getFullYear();
      const currentDate = getCurrentDate();

      // Dohvati postavke sustava
      const settings = await prisma.systemSettings.findFirst({
        where: { id: "default" },
      });

      // Koristi postavke ili zadane vrijednosti
      const cutoffMonth = settings?.renewalStartMonth || 10; // Default je studeni (10)
      const cutoffDay = settings?.renewalStartDay || 1;

      // Definiramo rok za obnovu lanstva (1. oujak tekue godine)
      const renewalDeadline = new Date(currentYear, 2, 1); // Mjesec 2 je oujak

      // Ako je trenutni datum nakon roka za obnovu, provjeri i zavri sva lanstva koja nisu obnovljena
      if (currentDate > renewalDeadline) {
        console.log(`INFO: Trenutni datum (${formatDate(currentDate)}) je nakon roka za obnovu lanstva (${formatDate(renewalDeadline)}). Pokreem provjeru isteklih lanstava za ${currentYear}.`);
        await membershipRepository.endExpiredMemberships(currentYear);
      } else {
        console.log(`INFO: Trenutni datum (${formatDate(currentDate)}) je prije roka za obnovu lanstva (${formatDate(renewalDeadline)}). Preskaem provjeru.`);
      }

      return;
